import React from "react";
import { Icon } from '@iconify/react';
import validator from 'validator';
 
function Papildymas() {

    const [values, setValues] = React.useState({});
    const handleInput = (event)=>{
        setValues(prev=>({...prev, [event.target.name]:[event.target.value]}));
        // console.log(values);
        // console.log(Object.keys(values).length);
    };
    const [errors, setErrors] = React.useState({});
    const validateNumber = (event) =>{
        if(!event.target.value){
            setErrors({[event.target.name]:"Įveskite korteles numerį"});
        }
        else{
                setErrors(!validator.isCreditCard(event.target.value)?{[event.target.name]:"Įveskite taisyklingą kortelės numerį"}:""); 
        }
    };
    const validateCvv = (event) => {
        if(!event.target.value){
            setErrors({[event.target.name]:"Įveskite CVV"});
        }
        else{
            setErrors(!validator.isByteLength(event.target.value, {
                min: 3,
                max: 3
            })?{[event.target.name]:"Cvv turi buti 3-simbolių ilgio"}:""); 
        }
    };
    const validateDate = (event) =>{
        if(!event.target.value){
            setErrors({[event.target.name]:"Įveskite datą"});
        }
    }


    return (
    <section className="main">
    <form action="/" className='main-content'>
        <a href="/" className="atgal"><Icon icon="ep:back" className="atgal-icon"/>atgal</a>
        <section className="form-item">
         <p>kortelės numeris: </p>
         <input type="text" name='number' onChange={e=>{handleInput(e); validateNumber(e)}} required placeholder='Įveskite kortelės numerį' className='form-input' id='main-card-number' value={values.number||""} maxLength = "16"/>
         <p className="error">{errors.number||""}</p>
        </section>
        <section className="form-item1">
            <section className="form-item1-element">
                <p>cvv: </p>
                <input type="text" name='cvv' onChange={e=>{handleInput(e); validateCvv(e)}}  required placeholder='cvv' className='form-input' id='main-card-cvv' value={values.cvv||""} maxLength = "3"/>
                <p className="error">{errors.cvv||""}</p>
            </section>
            <section className="form-item1-element">
         <p>data: </p>
         <input type="text" name='date' onChange={e=>{handleInput(e); validateDate(e)}} required placeholder='data' className='form-input' id='main-card-date' value = {values.date}/>
        </section>
     </section>
     <section className="form-item suma">
        <p>suma: </p>
        <input type="text" name='sum' required placeholder='0' className='form-input' id='main-card-number'/>
       </section>
        <section className="form-item">
           <button className='papil-button'><Icon icon="uil:money-insert" className='main-icon'/>Papildyti</button>
        </section>  
      </form>
    </section>);
}
 
export default Papildymas;
