import React from "react";
import { Icon } from '@iconify/react';
 
function Papildymas() {

    const [values, setValues] = React.useState({
        number:"",
        cvv:"",
        date:"",
        sum:""
    });
    const handleInput = (event)=>{
        setValues(prev=>({...prev, [event.target.name]:[event.target.value]}));
        console.log(values);
    }  

    return (
    <section className="main">
    <form action="/" className='main-content'>
        <a href="/" className="atgal"><Icon icon="ep:back" className="atgal-icon"/>atgal</a>
        <section className="form-item">
         <p>kortelės numeris: </p>
         <input type="text" name='number' onChange={handleInput()} required placeholder='Įveskite kortelės numerį' className='form-input' id='main-card-number' value={values.number||""}/>
        </section>
        <section className="form-item1">
            <section className="form-item1-element">
                <p>cvv: </p>
                <input type="text" name='cvv' required placeholder='cvv' className='form-input' id='main-card-cvv'/>
            </section>
            <section className="form-item1-element">
         <p>data: </p>
         <input type="text" name='date' required placeholder='data' className='form-input' id='main-card-date'/>
        </section>
     </section>
     <section className="form-item suma">
        <p>suma: </p>
        <input type="text" name='sum' required placeholder='0' className='form-input' id='main-card-number'/>
       </section>
        <section className="form-item">
           <button className='papil-button'><Icon icon="uil:money-insert" className='main-icon'/>Papildyti</button>
        </section>  
      </form>
    </section>);
}
 
export default Papildymas;
